INSERT INTO public.products (product_id, compatibility_specs, created_at, description, hidden, images, name, price, stock, updated_at, category_id, supplier_id) VALUES (19, '<p>Thông tin hàng hóa Model MP225V Xuất xứ Trung Quốc Thời điểm ra mắt 2024 Thời gian bảo hành 24 tháng Hướng dẫn bảo quản Để nơi khô ráo, nhẹ tay, dễ vỡ. Hướng dẫn sử dụng Xem trong sách hướng dẫn sử dụng Màu sắc Đen Thiết kế &amp; Trọng lượng Kích thước có chân đế 492.9 x 182.4 x 377.8 mm Trọng lượng sản phẩm có chân đế 4.3 kg Kiểu chân đế Vuông Thông số cơ bản Góc nhìn 178°(Dọc) / 178°(Ngang) Thời gian phản hồi 1ms Điện áp 100 - 240 V / 50 - 60 Hz Màn hình Loại màn hình LED Kích thước màn hình 21.5 inch Công nghệ màn hình VA Độ phân giải 1920 x 1080 Pixels Chuẩn màn hình FHD Độ sáng 300 nits Độ tương phản 1000:1 Tấm nền VA Tần số quét 100 Hz Tỷ lệ màn hình 16:09 Màu màn hình 1.07 tỷ Phụ kiện trong hộp Phụ kiện trong hộp Dây nguồn</p>', '2025-08-25 14:18:47.440875', '<p><strong>Lên kệ với mức giá vừa túi tiền, MSI Pro MP225V 21.5 inch là mẫu </strong><a href="https://fptshop.com.vn/man-hinh/van-phong"><strong>màn hình văn phòng</strong></a><strong> rất đáng sở hữu. Sản phẩm được trang bị tấm nền IPS độ phân giải Full HD sắc nét, có tần số quét 100Hz mượt mà và nhiều công nghệ bản vệ mắt. Đây là lựa chọn tốt để phục vụ công việc đồng thời đáp ứng nhu cầu giải trí cơ bản.</strong></p><p>&nbsp;</p><h2><strong>Kích thước nhỏ gọn, phù hợp mọi không gian</strong></h2><p>Với kích thước 21.5 inch, <a href="https://fptshop.com.vn/man-hinh/msi-pro-mp225v-21-5-inch-fhd-va-100hz"><strong>MSI Pro MP225V</strong></a> dễ dàng hòa hợp với bất kỳ không gian làm việc nào. Chân đế của sản phẩm được thiết kế tinh tế, vừa đảm bảo độ chắc chắn khi nâng đỡ tấm nền, vừa giúp tiết kiệm diện tích bàn làm việc, tạo thêm không gian cho các vật dụng cần thiết khác.</p><p>MSI cũng tối ưu hóa quy trình lắp ráp bằng cách thiết kế các bộ phận dễ tháo lắp mà không cần công cụ hỗ trợ. Nhờ vậy, bạn có thể thoải mái di chuyển, cài đặt hoặc bảo quản màn hình một cách dễ dàng, tiết kiệm thời gian và công sức.</p><p>&nbsp;</p><h2><strong>Hình ảnh sắc nét với tấm nền IPS cao cấp</strong></h2><p>MSI Pro MP225V sở hữu độ phân giải Full HD (1.920 x 1.080 pixels), mang đến khả năng hiển thị rõ ràng và sắc nét đến từng chi tiết. Với độ sáng tối đa 300 nit, <a href="https://fptshop.com.vn/man-hinh">màn hình</a> đảm bảo nội dung luôn dễ quan sát trong mọi điều kiện ánh sáng văn phòng.</p><p>Tấm nền IPS LCD cho góc nhìn rộng 178 độ, giúp bạn dễ dàng chia sẻ thông tin với đồng nghiệp trong các buổi họp. Cấu trúc viền mỏng góp phần tạo nên diện mạo hiện đại và giúp bạn tập trung hơn vào nội dung công việc. Ngoài ra, thiết kế viền siêu mỏng cũng lý tưởng để ghép nối nhiều màn hình, mở rộng không gian làm việc cho các chuyên gia thiết kế, lập trình viên hoặc những người làm công việc sáng tạo.&nbsp;</p><p>&nbsp;</p><h2><strong>Tận hưởng sự mượt mà với tần số quét 100Hz</strong></h2><p>Một trong những điểm nổi bật của MSI Pro MP225V là tần số quét lên đến 100Hz, mang lại trải nghiệm cuộn trang mượt mà hơn khi bạn cuộn trang để duyệt văn bản hoặc bảng biểu. Đây cũng là yếu tố quan trọng giúp giảm &nbsp;cảm giác mỏi mắt khi chúng ta làm việc với <a href="https://fptshop.com.vn/may-tinh-xach-tay">máy tính</a> nhiều giờ liên tục.</p><p>Tốc độ phản hồi 1ms giúp loại bỏ hiện tượng bóng mờ và nhòe hình khi xem phim, chơi game hoặc duyệt web. Nhờ đó, bạn sẽ có những phút giây giải trí trọn vẹn trong giờ nghỉ hoặc tối ưu công việc mà không bị gián đoạn.</p><p>&nbsp;</p><h2><strong>Công nghệ chăm sóc sức khỏe thị giác</strong></h2><p>Được thiết kế nhằm mang lại sự thoải mái tối đa cho người dùng, MSI Pro MP225V cung cấp công nghệ giảm ánh sáng xanh – nguyên nhân chính gây mỏi mắt khi sử dụng máy tính trong thời gian dài. Sản phẩm cũng hạn chế hiện tượng nháy hình ở tần số cao, bảo vệ đôi mắt khỏi những tác động tiêu cực khi làm việc liên tục.</p><p>Toàn bộ bề mặt tấm nền được phủ lớp chống chói hiệu quả, giúp giảm thiểu hiện tượng phản chiếu ánh sáng từ đèn hoặc môi trường bên ngoài. Yếu tố này đảm bảo bạn có thể làm việc thoải mái trong mọi điều kiện ánh sáng mà không bị chói mắt.</p>', false, '2017.jpg', 'Màn hình MSI Pro MP225V/21.5inch FHD(1920x1080)/VA 100Hz', 1490000.00, 3, '2025-08-25 20:50:05.42853', 12, 14);
